mapper.select:
SELECT aa.*, u.name approverName, a.name activityName
FROM activity_approval aa
LEFT JOIN user u ON aa.approver_id = u.id
LEFT JOIN activity a ON aa.activity_id = a.id


        aa.approval_status = #{approvalStatus}


        AND aa.activity_id = #{activityId}


        AND aa.approver_id = #{approverId}


ORDER BY aa.create_time DESC
